<script>
import Header from "../components/Header"
import Footer from "../components/Footer"

import {gsap} from 'gsap'

const
  horizontalScrolling = () => import('assets/scripts/horizontal-scroll'),
  slider = () => import('assets/scripts/slider.js'),
  lineOnScroll = () => import('assets/scripts/line-on-scroll.js')

const
  getSiteData = () => import('~/static/data.json').then(m => m.default || m),
  subscribeAPI = {
    url: 'https://db.designsucks.it',
    endpoint: '/wp-json/contact-form-7/v1/contact-forms/',
    formID: 5
  }

export default {
  components: {Header, Footer},
  async asyncData() {
    return await getSiteData().then(request => {
      const data = request
      return {data}
    })
  },
  data() {
    return {
      slider: null,
      sliderBackground: null,
      sliderTitle: null,
      sliderSubtitle: null,
      name: null,
      email: null,
      acceptedPolicy1: false,
      acceptedPolicy2: false,
      disabled: false,
      mailSent: false
    }
  },
  methods: {
    formSubmit: function () {
      this.disabled = true
      const formData = new FormData()

      const data = {
        "your-subject": 'Nuova richiesta di informazioni generali',
        "your-name": this.email,
        "your-email": this.email,
        "acceptance-448": this.acceptedPolicy1,
        'acceptance-949': this.acceptedPolicy2,
        "your-message": 'Nuova richiesta di iscrizione da: ' + this.email
      }

      for (const name in data) {
        formData.append(name, data[name]);
      }

      const options = {
        method: "POST",
        body: formData
      }

      fetch(
        subscribeAPI.url + subscribeAPI.endpoint + subscribeAPI.formID +
        "/feedback", options)
        .then((response) => response.json())
        .then((data) => {
          if (data.status === 'mail_sent') {
            this.mailSent = true
          } else if (data.status === 'validation_failed') {
            throw new Error(data.message);
          }
        })
        .catch((error) => {
          console.error("Error:", error)
        })
    }
  },
  mounted() {
    /*lineOnScroll().then(module => module.lineOnScroll())*/
    horizontalScrolling().then(module => module.horizontalScrollSections())
    slider().then(module => {
      this.sliderBackground = this.data.sectionHorizontal[4].slides[0].background
      this.sliderTitle = this.data.sectionHorizontal[4].slides[0].title
      this.sliderSubtitle = this.data.sectionHorizontal[4].slides[0].description


      this.slider = module.swiper(this.$refs.slider, {
        el: this.$refs.sliderPagination,
        clickable: true,
        type: 'bullets',
      }, () => {

        gsap.to(this.$refs.sliderBackground, {
          opacity: 0,
          onComplete: () => {
            this.sliderBackground = this.data.sectionHorizontal[4].slides[this.slider.activeIndex].background

            this.sliderTitle = this.data.sectionHorizontal[4].slides[this.slider.activeIndex].title
            this.sliderSubtitle = this.data.sectionHorizontal[4].slides[this.slider.activeIndex].description


            gsap.to(this.$refs.sliderBackground, {
              opacity: 1
            })
          }
        })
      })
    })
  }
}
</script>

